    let stripped_content = strip_ansi_codes(content);
    let padding = width.saturating_sub(stripped_content.chars().count());
    return format!(
        "\x1b[34m║\x1b[0m{}{}\x1b[34m║\x1b[0m",
        content,
        " ".repeat(padding)
    );
} 


